from sys import argv


def main(str):
    for i in range(0, len(str)-4):
        if len(set(str[i:i+4])) == 4:
            print(str[i:i+4])
            print(i+4)
            return


if __name__ == "__main__":
    main("bvwbjplbgvbhsrlpgdmjqwftvncz")
    main("nppdvjthqldpwncqszvftbrmjlhg")
    main("")

        