from sys import argv


def main(str):
    for i in range(0, len(str)-14):
        if len(set(str[i:i+14])) == 14:
            print(str[i:i+14])
            print(i+14)
            return


if __name__ == "__main__":
    main("bvwbjplbgvbhsrlpgdmjqwftvncz")
    main("nppdvjthqldpwncqszvftbrmjlhg")
    main("")

        